mapper.update:
update t_trip_log
set down_num = down_num + 1
where id = #{tripLogId}

update t_trip_log
set up_num = up_num + 1
where id = #{tripLogId}

mapper.select:
select *
from t_trip_log
where bus_teacher_id = #{busTeacherId}
  and trip_time = #{tripTime}

  and state = #{state}

  and time = CURRENT_DATE
  and deleted = false;
